import React, { Component } from 'react'
import weatherConfig from '../config/config'

let myData = [];

class WeatherBox extends Component {
    constructor(props){
        super(props);

        this.state={
            apiURL: weatherConfig.weatherData.weatherAPI.apiURL,
            api30DaysURL: weatherConfig.weatherData.weatherAPI.api30DaysURL,
            apiKey: weatherConfig.weatherData.weatherAPI.apiKey,
            imageURL: weatherConfig.weatherData.weatherAPI.imageURL,
            cities: weatherConfig.weatherData.weatherCities,
            weatherFetchData: []
        }

        this.get30DaysWeather=this.get30DaysWeather.bind(this)
    }

    getCitiesID(){
        const {cities}= this.state;
        cities.forEach((city) => {
            const{apiURL, apiKey} = this.state;
            let fetchURL= apiURL + "?id=" + city.id + "&appid=" + apiKey; 

            fetch(fetchURL).then((response) => {
                return response.json();
            }).then((response) => {
                myData.push(response);
                this.setState({
                    weatherFetchData: myData
                })
            });
        });

    }

    get30DaysWeather(getCityID){
        console.log(getCityID)

    }

    componentDidMount(){
        this.getCitiesID();
    }

    render() {

        return (
            <div className="appWeatherDatas">
                {
                    this.state.weatherFetchData.map((item, index) => {
                        return(
                            <div className="weatherItems" key={index}>
                                <div className="cityTitle">
                                    {item.name}
                                </div>
                                <div className="weatherIcon">
                                    <img src={this.state.imageURL + item.weather[0].icon + '.png'} />
                                    <span className="tempData">{item.main.temp} <sup>o</sup>C</span>
                                </div>
                                <div className="tempInfo">
                                    <span><b>En Yüksek: </b> {item.main.temp_max} <sup>o</sup></span>
                                    <span><b>En Düşük: </b> {item.main.temp_min}<sup>o</sup></span>
                                </div>
                                <div className="extraInfo">
                                    <span><b>Rüzgar: </b> {item.wind.speed}<sup> km/s</sup></span>
                                    <span><b>Nem: </b> {item.main.humidity}<sup> %</sup></span>
                                    <span><b>Bulut: </b> {item.clouds.all}<sup> %</sup></span>
                                </div>
                                <div className="btn30Days">
                                    <button onClick={() => this.get30DaysWeather(item.id)}>30 Günlük Hava Durumu İçin Tıklayın</button>
                                </div>
                            </div>
                        )
                    })
                }

            </div>
        )
    }
}

export default WeatherBox;
