import React, { Component } from 'react'
import weatherConfig from '../config/config'

let myData = [];

class WeatherBox extends Component {
    constructor(props){
        super(props);

        this.state={
            apiURL: weatherConfig.weatherData.weatherAPI.apiURL,
            apiKey: weatherConfig.weatherData.weatherAPI.apiKey,
            imageURL: weatherConfig.weatherData.weatherAPI.imageURL,
            cities: weatherConfig.weatherData.weatherCities,
            weatherFetchData: []
        }
    }

    getCitiesID(){
        const {cities}= this.state;
        cities.forEach((item) => {
            this.getFetchCitiesWeather(item.id)
        });

    }

    getFetchCitiesWeather(cityID){
        const{apiURL, apiKey} = this.state;
        let fetchURL= apiURL + "?id=" + cityID + "&appid=" + apiKey; 


        fetch(fetchURL).then((response) => {
            return response.json();
        }).then((response) => {
            console.log(response)
            myData.push(response)
        });
        console.log(myData)
        this.setState({
            weatherFetchData: myData
        });

    }

    componentDidMount(){
        this.getCitiesID();
    }

    render() {

        return (
            <div>
                {
                    setTimeout(() => {
                        this.state.weatherFetchData.map((item, index) => {
                            return(
                                <div>
                                    <h2>{item.name}</h2>
                                    <h3>OK</h3>
                                </div>
                            )
                        })
                    }, 500)
                }
            </div>
        )
    }
}

export default WeatherBox;
