import React, { Component } from 'react'
import weatherConfig from '../config/config'

let myData= [];
class Weather30Days extends Component {

    constructor(props){
        super(props);

        this.state={
            api30DaysURL: weatherConfig.weatherData.weatherAPI.api30DaysURL,
            apiKey: weatherConfig.weatherData.weatherAPI.apiKey,
            imageURL: weatherConfig.weatherData.weatherAPI.imageURL,
            weatherFetchData: [],
            myCityName: ''
        }

    }

    componentDidUpdate(){
        this.get30DaysWeather();
    }

    get30DaysWeather(){
        const {myCityID} = this.props;
        if(myCityID != "")
        {
            const{api30DaysURL, apiKey} = this.state;
            let fetchURL= api30DaysURL + "?id=" + myCityID + "&appid=" + apiKey; 

            fetch(fetchURL).then((response) => {
                return response.json();
            }).then((response) => {
                this.setState({
                    myCityName: response.city.name
                });
                return response.city;
            }).then((response) => {
                myData.push(response);
            });
        }
    }


    render() {
        return (
            <div className="col-12 mt-5">
                <h2>{this.state.myCityName != '' ? 'OK' : 'NO'}</h2>

            </div>
        )
    }
}

export default Weather30Days;
