import React, { Component } from 'react'
import weatherConfig from '../config/config'

class Weather30Days extends Component {

    constructor(props){
        super(props);
        this.state= {
            weather30DayFetchData: [],
            myCityName: '',
            api30DaysURL: weatherConfig.weatherData.weatherAPI.api30DaysURL,
        }
    }

    get30DaysWeather(){
        const {cityID} = this.props;
        let myCityName="";
        if(cityID != "")
        {
            const{api30DaysURL, apiKey} = this.state;
            let fetchURL= api30DaysURL + "?id=" + cityID + "&appid=" + apiKey; 

            fetch(fetchURL).then((response) => {
                return response.json();
            }).then((response) => {
                myCityName= response.city.name;
                return response.list;
            }).then((response) => {
                this.setState({
                    weather30DayFetchData: response,
                    myCityName: myCityName
                });
            }).catch((err) => {
                console.log('Bir hata oluştu.' + err);
            });  
        }
      }

      componentDidMount(){
          this.get30DaysWeather();
      }

    render() {
        const {myCityName, weatherFetchData } = this.props;
        return (
            <div className="col-12 mt-5">
                <h2>{myCityName != '' ? myCityName + ' ili 30 Günlük Hava Durumu' : ''}</h2>
                {this.props.cityID}
                {
                    // weatherFetchData.map((item, index) => {
                    //     return(
                    //         <div key={index}>
                    //             {item.temp.day}
                    //         </div>
                    //     )
                    // })
                }
            </div>
        )
    }
}

export default Weather30Days;
