import React, { Component } from 'react'
import weatherConfig from '../config/config'

export default class WeatherBox extends Component {

    constructor(props){
        super(props);

        this.state={
            apiURL: weatherConfig.weatherData.weatherAPI.apiURL,
            apiKey: weatherConfig.weatherData.weatherAPI.apiKey,
            imageURL: weatherConfig.weatherData.weatherAPI.imageURL,
            cities: weatherConfig.weatherData.weatherCities,
            weatherFetchData: []
        }
    }

    componentDidMount(){
        this.render();
    }

    componentWillMount(){
        this.getCitiesID();
    }

    getCitiesID(){
        const {cities}= this.state;
        cities.forEach((item) => {
            this.getFetchCitiesWeather(item.id)
        });

    }

    getFetchCitiesWeather(cityID){
        const{apiURL, apiKey} = this.state;
        let fetchURL= apiURL + "?id=" + cityID + "&appid=" + apiKey; 
        let myData = [];

        fetch(fetchURL).then((response) => {
            return response.json();
        }).then((response) => {
            console.log(response)
            this.setState({
                weatherFetchData: response
            });
        })

    }

    render() {

        return (
            <div>
                {
                    this.state.weatherFetchData.length
                }
            </div>
        )
    }
}
