import React, { Component } from 'react'
import weatherConfig from '../config/config'

let myData= [];
class Weather30Days extends Component {

    constructor(props){
        super(props);

        this.state={
            api30DaysURL: weatherConfig.weatherData.weatherAPI.api30DaysURL,
            apiKey: weatherConfig.weatherData.weatherAPI.apiKey,
            imageURL: weatherConfig.weatherData.weatherAPI.imageURL,
            weatherFetchData: [],
            myCityName: '',
            myCityID: ''
        }

    }

    componentDidMount(){
        //this.get30DaysWeather();
        console.log("A");
    }

    componentDidUpdate(){
        this.get30DaysWeather();
    }

    get30DaysWeather(){
        const {myCityID} = this.props;
        let myCityName="";
        if(myCityID != "")
        {
            console.log("AA");
            const{api30DaysURL, apiKey} = this.state;
            let fetchURL= api30DaysURL + "?id=" + myCityID + "&appid=" + apiKey; 

            fetch(fetchURL).then((response) => {
                return response.json();
            }).then((response) => {
                myCityName= response.city.name;
                return response.list;
            }).then((response) => {
                myData.push(response);
                this.setState({
                    myCityName: myCityName
                });

            }).catch((err) => {
                console.log('Bir hata oluştu.' + err);
            });
            console.log(myData);
            console.log(myCityName);

        }
    }


    render() {
        const {myCityName, weatherFetchData } = this.state;
        return (
            <div className="col-12 mt-5">
                <h2>{this.state.myCityName != '' ? this.state.myCityName + ' ili 30 Günlük Hava Durumu' : ''}</h2>
                {
                    weatherFetchData.map((item, index) => {
                        return(
                            <div key={index}>
                                {item.temp.day}
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}

export default Weather30Days;
